import React from "react";
import { Avatar, useChatContext } from "stream-chat-react";
import styled from 'styled-components'

import { HashIcon } from "../assets/HashIcon";

const EmptyState = () => {
  const { channel, client } = useChatContext();
  const members = Object.values(channel?.state?.members).filter(
    ({ user }) => user.id !== client.userID
  );

  const getAvatarGroup = () => {
    if (!members.length) return <Avatar size={72} />;

    return (
      <EmptyAvatars>
        {members.map((member, i) => {
          if (i > 2) return null;
          return (
            <Avatar
              key={i}
              image={member.user.image}
              name={member.user.name || member.user.id}
              size={72}
            />
          );
        })}
      </EmptyAvatars>
    );
  };

  const getUserText = () => {
    if (members.length === 1) {
      return (
        <span className="channel-empty__user-name">{`@${
          members[0].user.name || members[0].user.id
        }`}</span>
      );
    }

    if (members.length === 2) {
      return (
        <span className="channel-empty__user-name">{`@${
          members[0].user.name || members[0].user.id
        } and @${members[1].user.name || members[1].user.id}`}</span>
      );
    }

    let memberString = "";

    members.forEach((member, i) => {
      if (i !== members.length - 1) {
        memberString = `${memberString}@${
          member.user.name || member.user.id
        }, `;
      } else {
        memberString = `${memberString} and @${
          member.user.name || member.user.id
        }`;
      }
    });

    return (
      <span className="channel-empty__user-name">
        {memberString || "the Universe"}
      </span>
    );
  };

  return (
    <EmptyContainer>
      {channel.type === "team" ? <HashIcon /> : getAvatarGroup()}
      <EmptyFirst>
        This is the beginning of your chat history
        {channel.type === "team" ? " in " : " with "}
        {channel.type === "team"
          ? `#${channel.data.name || channel.data.id}`
          : getUserText()}
        .
      </EmptyFirst>
      <EmptySecond>
        Send messages, attachments, links, emojis, and more.
      </EmptySecond>
    </EmptyContainer>
  );
};

export default EmptyState

const EmptyContainer = styled.div`
  display: flex;
  height: 100%;
  flex-direction: column;
  justify-content: flex-end;
  margin-left: 20px;
  margin-right: 20px;
  padding-bottom: 20px;
`

const EmptyAvatars = styled.div`
  display: flex;
`

const EmptyFirst = styled.div`
  z-index: 3;
`

const EmptySecond = styled.div`
  position: relative;
  right: 32px;
  z-index: 2;
`;
